import React from 'react';
import ReactDOM from 'react-dom';
import './ProfileDropdown.css';

const race = ['Azure Female', 'Iron Dwarf', 'Highborn Human', 'Lowland Human', 'Mountain Dwarf', 'Scythian Elf', 'Woodland Elf'];

const ProfileDropdown = () => {
  const [isOpen, setOpen] = React.useState(false);
  const [selectedItem, setSelectedItem] = React.useState(null);

  const toggleDropdown = () => setOpen(!isOpen);

  const handleItemClick = (raceItem) => {
    setSelectedItem(raceItem);
    setOpen(false);
  };

  const itemList = race.map((item, index) => (
    <div
      onClick={() => handleItemClick(item)}
      className="dropdown__item"
      key={index}>
      {item}
    </div>
  ));

  return (
    <div className="dropdown">
      <div className="dropdown-header" onClick={toggleDropdown}>
        {selectedItem ? selectedItem : "Select your race"}
        <i className={`fa fa-chevron-right icon ${isOpen && "open"}`}></i>
      </div>
      <div className={`dropdown-body ${isOpen && "open"}`}>
        {itemList}
      </div>
    </div>
  );
};

ReactDOM.render(
  <ProfileDropdown />,
  document.getElementById('root')
);

export default ProfileDropdown;
