import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Swal from 'sweetalert2';
import './style.css';
import UserDropdown from './UserDropdown';
import './style.css';

const Departement1 = () => {
  const [darkMode, setDarkMode] = useState(false);
  const [user, setUser] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      console.error('Token is missing');
      return;
    }

    const fetchUserData = async () => {
      const userId = localStorage.getItem('userId');
      if (!userId) {
        console.error('User ID is missing');
        return;
      }

      try {
        const response = await fetch(`http://localhost:8080/api/users/${userId}`, {
          headers: {
            'Authorization': 'Bearer ' + token,
          }
        });

        if (!response.ok) {
          throw new Error('Failed to fetch user data');
        }

        const user = await response.json();
        setUser(user);
        console.log(user);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, []);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
    document.body.classList.toggle('dark-mode', !darkMode);
  };

  const navigateTo = (path) => {
    // Vérifier si l'état utilisateur est vide
    if (Object.keys(user).length === 0) {
      alert('Chargement en cours. Veuillez patienter.');
      return;
    }

    const userRole = user.role || [];
    let allowed = false;

    switch(path) {
      case '/chef1':
        allowed = userRole.includes('Chef departement');
        break;
      case '/assistant1':
        allowed = userRole.includes('Assistant département') || userRole.includes('Chef departement');
        break;
      case '/commercial':
