import { Link, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import Swal from 'sweetalert2';




const Dashboard = () => {

    const [showRegistrationForm, setShowRegistrationForm] = useState(false);

    const toggleRegistrationForm = () => {
        setShowRegistrationForm(!showRegistrationForm);
    };

    const [darkMode, setDarkMode] = useState(false);
    const [themeColor, setThemeColor] = useState('default'); // Default theme color

    const toggleDarkMode = () => {
        setDarkMode(!darkMode);
    };

    const changeThemeColor = (theme) => {
        setThemeColor(theme);
    };


    const [user, setUser] = useState(null);
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

   



    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            Swal.fire({
                icon: 'error',
                title: 'Unauthorized Access',
                text: 'You are not authorized to access this page.',
                showConfirmButton: false,
                timer: 1500
            });
            navigate('/login');
            return;
        }

        fetch('http://localhost:8080/api/verify-token', {
            method: 'GET',
            headers: {
                'Authorization': 'Bearer ' + token,
            }
        })
        .then(response => response.json())
        .then(data => {
            if (data.status && !data.isAdmin) {
                Swal.fire({
                    icon: 'error',
                    title: 'Unauthorized Access',
                    text: 'You are not authorized to access this page.',
                    showConfirmButton: false,
                    timer: 1500
                });
                navigate('/login');
                return;
            }

            const userId = localStorage.getItem('userId');
            if (!userId) {
                console.error('User ID is missing');
                return;
            }

            fetch(`http://localhost:8080/api/users/${userId}`)
                .then(response => response.json())
                .then(data => {
                    if (data.status) {
                        setUser(data.data);
                    } else {
                        console.error('Failed to fetch user details:', data.message);
                    }
                })
                .catch(error => console.error('Error:', error));

            fetch('http://localhost:8080/api/users', {
                method: 'GET',
                headers: {
                    'Authorization': 'Bearer ' + localStorage.getItem('token'),
                }
            })
            .then(response => response.json())
            .then(data => {
                if (data.status) {
                    const filteredUsers = data.data.filter(user => user.email !== 'admin@itsolutionstuff.com');
                    setUsers(filteredUsers);
                } else {
                    console.error('Failed to fetch user data:', data.message);
                }
            })
            .catch(error => console.error('Error fetching data:', error))
            .finally(() => setLoading(false));
        })
        .catch(error => console.error('Error:', error));
    }, [navigate]);

    const handleLogout = () => {
        Swal.fire({
            title: 'Are you sure?',
            text: 'Do you really want to log out?',
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Yes, log out!'
        }).then((result) => {
            if (result.isConfirmed) {
                const token = localStorage.getItem('token');
                if (!token) {
                    console.error('No token found in localStorage');
                    return;
                }

                fetch('http://localhost:8080/api/logout', {
                    method: 'GET',
                    headers: {
                        'Authorization': 'Bearer ' + token,
                        'Content-Type': 'application/json',
                        'Accept': 'application/json',
                    },
                })
                .then(response => {
                    if (response.status === 401) {
                        throw new Error('Unauthorized');
                    }
                    return response.json();
                })
                .then(data => {
                    if (data.status) {
                        localStorage.removeItem('token');
                        Swal.fire({
                            icon: 'success',
                            title: 'Logged Out',
                            text: 'You have been successfully logged out.',
                            showConfirmButton: false,
                            timer: 1500
                        }).then(() => {
                            navigate('/login');
                        });
                    }
                })
                .catch(error => {
                    console.error('Error:', error);
                });
            }
        });
    };

    const handleDelete = (userId) => {
        Swal.fire({
            title: 'Are you sure?',
            text: 'You will not be able to recover this user!',
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Yes, delete it!'
        }).then((result) => {
            if (result.isConfirmed) {
                fetch(`http://localhost:8080/api/users/${userId}`, {
                    method: 'DELETE',
                    headers: {
                        'Authorization': 'Bearer ' + localStorage.getItem('token')
                    }
                })
                .then(response => response.json())
                .then(result => {
                    if (result.status) {
                        setUsers(users.filter(user => user.id !== userId));
                        Swal.fire('Deleted!', 'User has been deleted.', 'success');
                    } else {
                        Swal.fire('Error!', 'Failed to delete user.', 'error');
                    }
                })
                .catch(error => console.error('Error deleting user:', error));
            }
        });
    };

    if (loading) {
        return <div>Loading...</div>;
    }
    
    
    return (

        



        <div id="page-container" className={`sidebar-o sidebar-dark enable-page-overlay side-scroll page-header-fixed main-content-narrow ${darkMode ? 'dark-mode' : ''}`}>

            <aside id="side-overlay">
                <div className="content-header border-bottom"></div>
                





                <a className="img-link me-1" >
                    <img className="img-avatar img-avatar32" src="assets/media/avatars/avatar10.jpg" alt="" />
                </a>
                <div className="content-side"></div>
            </aside>

            <nav id="sidebar" aria-label="Main Navigation">
  <div className="content-header">
    
    <a className="fw-semibold text-dual" href="index.html">
      <span className="smini-visible">
        <i className="fa fa-circle-notch text-primary"></i>
      </span>
      <span className="smini-hide fs-5 tracking-wider">Transit Master</span>
    </a>
    <div>
    <button type="button" className="btn btn-sm btn-alt-secondary" data-toggle="layout" data-action="dark_mode_toggle" onClick={toggleDarkMode}>
                <i className="far fa-moon"></i>
                {/* Visually hide the button text */}
                <span className="visually-hidden">Toggle Dark Mode</span>
            </button>
      <div className="dropdown d-inline-block ms-1">
      <button type="button" className="btn btn-sm btn-alt-secondary" id="sidebar-themes-dropdown" data-bs-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                    <i className="fa fa-brush"></i>
                </button>
                <div className="dropdown-menu dropdown-menu-end fs-sm smini-hide border-0" aria-labelledby="sidebar-themes-dropdown">
                <a className="dropdown-item" onClick={() => changeThemeColor('default')} href="#">
                        Default
                    </a>
                    <a className="dropdown-item" onClick={() => changeThemeColor('amethyst')} href="#">
                        Amethyst
                    </a>
          <a className="dropdown-item d-flex align-items-center justify-content-between fw-medium" data-toggle="theme" data-theme="assets/css/themes/city.min.css" href="#">
            <span>City</span>
            <i className="fa fa-circle text-city"></i>
          </a>
          <a className="dropdown-item d-flex align-items-center justify-content-between fw-medium" data-toggle="theme" data-theme="assets/css/themes/flat.min.css" href="#">
            <span>Flat</span>
            <i className="fa fa-circle text-flat"></i>
          </a>
          <a className="dropdown-item d-flex align-items-center justify-content-between fw-medium" data-toggle="theme" data-theme="assets/css/themes/modern.min.css" href="#">
            <span>Modern</span>
            <i className="fa fa-circle text-modern"></i>
          </a>
          <a className="dropdown-item d-flex align-items-center justify-content-between fw-medium" data-toggle="theme" data-theme="assets/css/themes/smooth.min.css" href="#">
            <span>Smooth</span>
            <i className="fa fa-circle text-smooth"></i>
          </a>
          <div className="dropdown-divider"></div>
          <a className="dropdown-item fw-medium" data-toggle="layout" data-action="sidebar_style_light" href="javascript:void(0)">
            <span>Sidebar Light</span>
          </a>
          <a className="dropdown-item fw-medium" data-toggle="layout" data-action="sidebar_style_dark" href="javascript:void(0)">
            <span>Sidebar Dark</span>
          </a>
          <div className="dropdown-divider"></div>
          <a className="dropdown-item fw-medium" data-toggle="layout" data-action="header_style_light" href="javascript:void(0)">
            <span>Header Light</span>
          </a>
          <a className="dropdown-item fw-medium" data-toggle="layout" data-action="header_style_dark" href="javascript:void(0)">
            <span>Header Dark</span>
          </a>
        </div>
      </div>
      <a className="d-lg-none btn btn-sm btn-alt-secondary ms-1" data-toggle="layout" data-action="sidebar_close" href="javascript:void(0)">
        <i className="fa fa-fw fa-times"></i>
      </a>
    </div>
  </div>
  <div className="js-sidebar-scroll">
    <div className="content-side">
      <ul className="nav-main">
        <li className="nav-main-item">
          <a className="nav-main-link" href="be_pages_dashboard.html">
            <i className="nav-main-link-icon si si-speedometer"></i>
            <span className="nav-main-link-name">Dashboard</span>
          </a>
        </li>
      </ul>
    </div>
  </div>
</nav>


            <header id="page-header">
                {/* Header Content */}
                <div className="content-header">
                    {/* Left Section */}
                    <div className="d-flex align-items-center">
                        {/* Toggle Sidebar */}
                        {/* Layout API, functionality initialized in Template._uiApiLayout()*/}
                        <button type="button" className="btn btn-sm btn-alt-secondary me-2 d-lg-none" data-toggle="layout" data-action="sidebar_toggle">
                            <i className="fa fa-fw fa-bars"></i>
                        </button>
                        {/* END Toggle Sidebar */}

                        {/* Open Search Section (visible on smaller screens) */}
                        {/* Layout API, functionality initialized in Template._uiApiLayout() */}
                        <button type="button" className="btn btn-sm btn-alt-secondary d-md-none" data-toggle="layout" data-action="header_search_on">
                            <i className="fa fa-fw fa-search"></i>
                        </button>
                        {/* END Open Search Section */}

                        {/* Search Form (visible on larger screens) */}
                        <form className="d-none d-md-inline-block" action="be_pages_generic_search.html" method="POST">
                            <div className="input-group input-group-sm">
                                <input type="text" className="form-control form-control-alt" placeholder="Search.." id="page-header-search-input2" name="page-header-search-input2" />
                                <span className="input-group-text border-0">
                                    <i className="fa fa-fw fa-search"></i>
                                </span>
                            </div>
                        </form>
                        {/* END Search Form */}
                    </div>
                    {/* END Left Section */}

                    {/* Right Section */}
                    <div className="d-flex align-items-center">
                        {/* User Dropdown */}
                         {/* User Dropdown */}
                         <div className="dropdown d-inline-block ms-2">
                            <button type="button" className="btn btn-sm btn-alt-secondary d-flex align-items-center" id="page-header-user-dropdown" data-bs-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                                <img className="rounded-circle" src="assets/media/avatars/avatar10.jpg" alt="Header Avatar" style={{ width: '21px' }} />
                                <span className="d-none d-sm-inline-block ms-2">{user ? user.name : ''}</span>
                                <i className="fa fa-fw fa-angle-down d-none d-sm-inline-block opacity-50 ms-1 mt-1"></i>
                            </button>
                            <div className="dropdown-menu dropdown-menu-md dropdown-menu-end p-0 border-0" aria-labelledby="page-header-user-dropdown">
                                <div className="p-3 text-center bg-body-light border-bottom rounded-top">
                                    <img className="img-avatar img-avatar48 img-avatar-thumb" src="assets/media/avatars/avatar10.jpg" alt="" />
                                    <p className="mt-2 mb-0 fw-medium">{user.name}</p>
                                    <p className="mt-2 mb-0 fw-medium">{user.role ? user.role.join(', ') : ''}</p>
                                </div>
                                <div className="p-2">
                                    <a id="logout-link" className="dropdown-item d-flex align-items-center justify-content-between" href="#">
                                        <span className="fs-sm fw-medium">Log Out</span>
                                    </a>
                                </div>
                            </div>
                        </div>
                    </div>
                    {/* END Right Section */}

                    {/* Header Search */}
                    <div id="page-header-search" className="overlay-header bg-body-extra-light">
                        <div className="content-header">
                            <form className="w-100" action="be_pages_generic_search.html" method="POST">
                                <div className="input-group">
                                    {/* Layout API, functionality initialized in Template._uiApiLayout() */}
                                    <button type="button" className="btn btn-alt-danger" data-toggle="layout" data-action="header_search_off">
                                        <i className="fa fa-fw fa-times-circle"></i>
                                    </button>
                                    <input type="text" className="form-control" placeholder="Search or hit ESC.." id="page-header-search-input" name="page-header-search-input" />
                                </div>
                            </form>
                        </div>
                    </div>
                    {/* END Header Search */}

                    {/* Header Loader */}
                    {/* Please check out the Loaders page under Components category to see examples of showing/hiding it */}
                    <div id="page-header-loader" className="overlay-header bg-body-extra-light">
                        <div className="content-header">
                            <div className="w-100 text-center">
                            <i className="fa fa-fw fa-circle-notch fa-spin"></i>
              </div>
            </div>
          </div>
          {/* END Header Loader */}
        </div>
        {/* END Header Content */}
      </header>
      {/* END Header */}
      <main id="main-container">
                <div className="bg-body-light">
                    <div className="content content-full">
                        <div className="d-flex flex-column flex-sm-row justify-content-sm-between align-items-sm-center py-2">
                            <div className="flex-grow-1">
                                <h1 className="h3 fw-bold mb-1">
                                    Liste des employs
                                </h1>
                                <h2 className="fs-base lh-base fw-medium text-muted mb-0"></h2>
                            </div>
                            <nav className="flex-shrink-0 mt-3 mt-sm-0 ms-sm-3" aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt">
                                    <li className="breadcrumb-item">
                                    <a className="link-fx" href="#" onClick={toggleRegistrationForm}>Ajouter un nouvel utilisateur</a>
            {showRegistrationForm && <RegistrationForm toggleRegistrationForm={toggleRegistrationForm} />}
            {showRegistrationForm && <div className="modal-overlay"></div>}
            <style>
                {`
                    .modal-overlay {
                        position: fixed;
                        top: 0;
                        left: 0;
                        width: 100%;
                        height: 100%;
                        background-color: rgba(0, 0, 0, 0.5); /* semi-transparent black */
                        backdrop-filter: blur(5px); /* apply a blur effect to the background */
                        z-index: 1000; /* ensure it's on top of everything else */
                    }

                    .registration-form {
                        position: fixed;
                        top: 50%;
                        left: 50%;
                        transform: translate(-50%, -50%);
                        background-color: white;
                        padding: 20px;
                        border-radius: 5px;
                        z-index: 1001; /* ensure it's above the overlay */
                    }

                    .exit-button {
                        position: absolute;
                        top: 10px;
                        right: 10px;
                        cursor: pointer;
                    }
                `}
            </style>
                                    </li>
                                    <li className="breadcrumb-item" aria-current="page"></li>
                                </ol>
                            </nav>
                        </div>
                    </div>
                </div>

                <div className="content">
                    <div className="block block-rounded">
                        <div className="block-header block-header-default">
                            <h3 className="block-title"></h3>
                            <div className="block-options">
                                <button type="button" className="btn-block-option">
                                    <i className="si si-settings"></i>
                                </button>
                            </div>
                        </div>
                        <div className="block-content">
                            <p className="fs-sm text-muted"></p>
                            <div className="table-responsive">
                                <table className="table table-bordered table-striped table-vcenter" id="userTable">
                                    <thead>
                                        <tr>
                                            <th className="text-center" style={{ width: "100px" }}>
                                                <i className="far fa-user"></i>
                                            </th>
                                            <th className="fw-semibold fs-sm" style={{ width: "20%" }}>Name</th>
                                            <th>Surname</th>
                                            <th>Department</th>
                                            <th style={{ width: "20%" }}>Role</th>
                                            <th style={{ width: "40%" }}>Phone Number</th>
                                            <th style={{ width: "20%" }}>Email</th>
                                            <th className="text-center" style={{ width: "100px" }}>Actions</th>
                                        </tr>
                                    </thead>
                                    <tbody>
    {users.map(user => (
        <tr key={user.id}>
            <td className="text-center">
                <img className="img-avatar img-avatar48" src="assets/media/avatars/avatar1.jpg" alt="User Avatar" />
            </td>
            <td>{user.name}</td>
                                                <td>{user.surname}</td>
                                                <td>
                                                <span className="fs-xs fw-semibold d-inline-block py-1 px-3 rounded-pill bg-primary-light text-primary">{user.departement ? user.departement.join(', ') : ''}</span></td>
                                                <td>
                                                <span className="fs-xs fw-semibold d-inline-block py-1 px-3 rounded-pill bg-info-light text-info">{user.role ? user.role.join(', ') : ''}</span></td>
                                                <td>{user.phone_number}</td>
                                                <td>{user.email}</td>
                                                <td>
    <div className="d-flex">
        <Link to={`/edit/${user.id}`} className="btn btn-sm btn-alt-secondary edit-button me-1" data-bs-toggle="tooltip" title="Edit">
            <i className="fa fa-fw fa-pencil-alt"></i>
        </Link>
        <button type="button" className="btn btn-sm btn-alt-secondary delete-button" onClick={() => handleDelete(user.id)} data-bs-toggle="tooltip" title="Delete">
            <i className="fa fa-fw fa-times"></i>
        </button>
    </div>
</td>
        </tr>
    ))}
</tbody>
                                </table>
                            </div>
                        </div>
                    </div>
                </div>
            </main>

            <footer id="page-footer" className="bg-body-light">
                <div className="content py-3">
                    <div className="row fs-sm">
                        {/* Footer content */}
                    </div>
                </div>
            </footer>

        </div>
    );
}

const RegistrationForm = ({ toggleRegistrationForm }) => {
    const [formData, setFormData] = useState({
        name: '',
        surname: '',
        email: '',
        phone: '',
        department: '1', // Default department
        roles: [],
        password: '',
    });

    const updateFormData = (key, value) => {
        setFormData({ ...formData, [key]: value });
    };

    const handleRoleChange = (e) => {
        const roleId = e.target.value;
        if (formData.roles.includes(roleId)) {
            setFormData({
                ...formData,
                roles: formData.roles.filter((id) => id !== roleId),
            });
        } else {
            setFormData({
                ...formData,
                roles: [...formData.roles, roleId],
            });
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        const requestData = {
            name: formData.name,
            surname: formData.surname,
            email: formData.email,
            phone_number: formData.phone,
            password: formData.password,
            role_ids: formData.roles,
            departement_ids: [parseInt(formData.department)], // Ensure this is an integer
        };

        fetch('http://localhost:8080/api/inscription', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(requestData),
        })
            .then((response) => response.json())
            .then((data) => {
                if (data.status) {
                    Swal.fire({
                        icon: 'success',
                        title: 'Success',
                        text: 'User created successfully',
                    });
                    // Clear form fields after successful submission
                    setFormData({
                        name: '',
                        surname: '',
                        email: '',
                        phone: '',
                        department: '1',
                        roles: [],
                        password: '',
                    });
                } else {
                    Swal.fire({
                        icon: 'error',
                        title: 'Error',
                        text: data.message,
                    });
                    console.error(data.errors);
                }
            })
            .catch((error) => {
                Swal.fire({
                    icon: 'error',
                    title: 'Error',
                    text: 'An error occurred. Please try again.',
                });
                console.error('Error:', error);
            });
    };

    return (
        <div className="registration-form">
            <button className="exit-button" onClick={toggleRegistrationForm}>
                X
            </button>
            <form onSubmit={handleSubmit}>
                <label>Name:</label>
                <input
                    type="text"
                    value={formData.name}
                    onChange={(e) => updateFormData('name', e.target.value)}
                    name="name"
                />
                <label>Surname:</label>
                <input
                    type="text"
                    value={formData.surname}
                    onChange={(e) => updateFormData('surname', e.target.value)}
                    name="surname"
                />
                <label>Email:</label>
                <input
                    type="email"
                    value={formData.email}
                    onChange={(e) => updateFormData('email', e.target.value)}
                    name="email"
                />
                <label>Phone Number:</label>
                <input
                    type="text"
                    value={formData.phone}
                    onChange={(e) => updateFormData('phone', e.target.value)}
                    name="phone"
                />
                <label>Department:</label>
                <div className="space-x-2">
                    <div className="form-check form-check-inline">
                        <input
                            className="form-check-input"
                            type="radio"
                            value="1"
                            checked={formData.department === '1'}
                            onChange={(e) => updateFormData('department', e.target.value)}
                            id="department1"
                            name="department"
                        />
                        <label className="form-check-label" htmlFor="department1">
                            Department Commercial
                        </label>
                    </div>
                    <div className="form-check form-check-inline">
                        <input
                            className="form-check-input"
                            type="radio"
                            value="2"
                            checked={formData.department === '2'}
                            onChange={(e) => updateFormData('department', e.target.value)}
                            id="department2"
                            name="department"
                        />
                        <label className="form-check-label" htmlFor="department2">
                            Department Operations
                        </label>
                    </div>
                    <div className="form-check form-check-inline">
                        <input
                            className="form-check-input"
                            type="radio"
                            value="3"
                            checked={formData.department === '3'}
                            onChange={(e) => updateFormData('department', e.target.value)}
                            id="department3"
                            name="department"
                        />
                        <label className="form-check-label" htmlFor="department3">
                            Department Facturations
                        </label>
                    </div>
                </div>

                {/* Roles */}
                <div className="col-12" id="roles-container">
                    {formData.department === '1' && (
                        <>
                            <label className="form-label">Role</label>
                            <div className="space-x-2">
                                <div className="form-check form-check-inline">
                                    <input
                                        className="form-check-input"
                                        type="checkbox"
                                        value="1"
                                        id="role1"
                                        checked={formData.roles.includes('1')}
                                        onChange={handleRoleChange}
                                        name="role1"
                                    />
                                    <label className="form-check-label" htmlFor="role1">
                                        Chef departement
                                    </label>
                                </div>
                                <div className="form-check form-check-inline">
                                    <input
                                        className="form-check-input"
                                        type="checkbox"
                                        value="7"
                                        id="role7"
                                        checked={formData.roles.includes('7')}
                                        onChange={handleRoleChange}
                                        name="role7"
                                    />
                                    <label className="form-check-label" htmlFor="role7">
                                        Assistant dpartement
                                    </label>
                                </div>
                                <div className="form-check form-check-inline">
                                    <input
                                        className="form-check-input"
                                        type="checkbox"
                                        value="2"
                                        id="role2"
                                        checked={formData.roles.includes('2')}
                                        onChange={handleRoleChange}
                                        name="role2"
                                    />
                                    <label className="form-check-label" htmlFor="role2">
                                        Commercial
                                    </label>
                                </div>
                            </div>
                        </>
                    )}
                    {/* Add similar blocks for other departments */}
                </div>

                <label>Password:</label>
                <input
                    type="password"
                    value={formData.password}
                    onChange={(e) => updateFormData('password', e.target.value)}
                    name="password"
                />
                <button type="submit">Register</button>
            </form>
        </div>
    );
};



export default Dashboard;
