import { Link, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import Swal from 'sweetalert2';




const Dashboard = () => {

    const [darkMode, setDarkMode] = useState(false);
    const [themeColor, setThemeColor] = useState('default'); // Default theme color

    const toggleDarkMode = () => {
        setDarkMode(!darkMode);
    };

    const changeThemeColor = (theme) => {
        setThemeColor(theme);
    };


    const [user, setUser] = useState(null);
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

    const [isDropdownOpen, setIsDropdownOpen] = useState(false);
    const toggleDropdown = () => {
        setIsDropdownOpen(!isDropdownOpen);
    };
    
    const closeDropdown = () => {
        setIsDropdownOpen(false);
    };



    useEffect(() => {
        const handleDocumentClick = (event) => {
            if (!event.target.closest('#page-header-user-dropdown')) {
                setIsDropdownOpen(false);
            }
        };
    
        document.addEventListener('click', handleDocumentClick);
    
      
        const token = localStorage.getItem('token');
        if (!token) {
            Swal.fire({
                icon: 'error',
                title: 'Unauthorized Access',
                text: 'You are not authorized to access this page.',
                showConfirmButton: false,
                timer: 1500
            });
            navigate('/login');
            return;
        }

        fetch('http://localhost:8080/api/verify-token', {
            method: 'GET',
            headers: {
                'Authorization': 'Bearer ' + token,
            }
        })
        .then(response => response.json())
        .then(data => {
            if (data.status && !data.isAdmin) {
                Swal.fire({
                    icon: 'error',
                    title: 'Unauthorized Access',
                    text: 'You are not authorized to access this page.',
                    showConfirmButton: false,
                    timer: 1500
                });
                navigate('/login');
                return;
            }

            const userId = localStorage.getItem('userId');
            if (!userId) {
                console.error('User ID is missing');
                return;
            }

            fetch(`http://localhost:8080/api/users/${userId}`)
                .then(response => response.json())
                .then(data => {
                    if (data.status) {
                        setUser(data.data);
                    } else {
                        console.error('Failed to fetch user details:', data.message);
                    }
                })
                .catch(error => console.error('Error:', error));

            fetch('http://localhost:8080/api/users', {
                method: 'GET',
                headers: {
                    'Authorization': 'Bearer ' + localStorage.getItem('token'),
                }
            })
            .then(response => response.json())
            .then(data => {
                if (data.status) {
                    const filteredUsers = data.data.filter(user => user.email !== 'admin@itsolutionstuff.com');
                    setUsers(filteredUsers);
                } else {
                    console.error('Failed to fetch user data:', data.message);
                }
            })
            .catch(error => console.error('Error fetching data:', error))
            .finally(() => setLoading(false));
        })
        .catch(error => console.error('Error:', error));
        return () => {
            document.removeEventListener('click', handleDocumentClick);
        };
    }, [navigate, setIsDropdownOpen, setUser, setUsers, setLoading]);
    

    const handleLogout = () => {
        Swal.fire({
            title: 'Are you sure?',
            text: 'Do you really want to log out?',
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Yes, log out!'
        }).then((result) => {
            if (result.isConfirmed) {
                const token = localStorage.getItem('token');
                if (!token) {
                    console.error('No token found in localStorage');
                    return;
                }

                fetch('http://localhost:8080/api/logout', {
                    method: 'GET',
                    headers: {
                        'Authorization': 'Bearer ' + token,
                        'Content-Type': 'application/json',
                        'Accept': 'application/json',
                    },
                })
                .then(response => {
                    if (response.status === 401) {
                        throw new Error('Unauthorized');
                    }
                    return response.json();
                })
                .then(data => {
                    if (data.status) {
                        localStorage.removeItem('token');
                        Swal.fire({
                            icon: 'success',
                            title: 'Logged Out',
                            text: 'You have been successfully logged out.',
                            showConfirmButton: false,
                            timer: 1500
                        }).then(() => {
                            navigate('/login');
                        });
                    }
                })
                .catch(error => {
                    console.error('Error:', error);
                });
            }
        });
    };

    const handleDelete = (userId) => {
        Swal.fire({
            title: 'Are you sure?',
            text: 'You will not be able to recover this user!',
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Yes, delete it!'
        }).then((result) => {
            if (result.isConfirmed) {
                fetch(`http://localhost:8080/api/users/${userId}`, {
                    method: 'DELETE',
                    headers: {
                        'Authorization': 'Bearer ' + localStorage.getItem('token')
                    }
                })
                .then(response => response.json())
                .then(result => {
                    if (result.status) {
                        setUsers(users.filter(user => user.id !== userId));
                        Swal.fire('Deleted!', 'User has been deleted.', 'success');
                    } else {
                        Swal.fire('Error!', 'Failed to delete user.', 'error');
                    }
                })
                .catch(error => console.error('Error deleting user:', error));
            }
        });
    };

    if (loading) {
        return <div>Loading...</div>;
    }
    
    
    return (

        



        <div id="page-container" className={`sidebar-o sidebar-dark enable-page-overlay side-scroll page-header-fixed main-content-narrow ${darkMode ? 'dark-mode' : ''} ${themeColor}`}>
            <aside id="side-overlay">
                <div className="content-header border-bottom"></div>
                





                <a className="img-link me-1" >
                    <img className="img-avatar img-avatar32" src="assets/media/avatars/avatar10.jpg" alt="" />
                </a>
                <div className="content-side"></div>
            </aside>

            <nav id="sidebar" aria-label="Main Navigation">
  <div className="content-header">
    
    <a className="fw-semibold text-dual" href="index.html">
      <span className="smini-visible">
        <i className="fa fa-circle-notch text-primary"></i>
      </span>
