import React, { useState } from 'react';
import { Link, useNavigate } from "react-router-dom";
import Swal from 'sweetalert2';
import './style.css';
import UserDropdown from './UserDropdown';

const Departement1 = () => {
    const [darkMode, setDarkMode] = useState(false);
    const [userRole, setUserRole] = useState([]);
    const [userName, setUserName] = useState('');
    const [userFullName, setUserFullName] = useState('');
    const navigate = useNavigate();
  
    const toggleDarkMode = () => {
      setDarkMode(!darkMode);
      document.body.classList.toggle('dark-mode', !darkMode);
    };
  
    useEffect(() => {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/login');
        return;
      }
  
      const userId = localStorage.getItem('userId');
      if (userId) {
        fetch(`http://localhost:8080/api/users/${userId}`, {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        })
          .then(response => response.json())
          .then(data => {
            if (data.status) {
              const user = data.data;
              setUserName(user.name);
              setUserFullName(`${user.name}`);
              setUserRole(user.role);
              localStorage.setItem('userRole', user.role.join(', '));
            } else {
              console.error('Failed to fetch user details:', data.message);
            }
          })
          .catch(error => console.error('Error:', error));
      }
    }, [navigate]);
  
    const navigateTo = (page) => {
      let allowed = false;
  
      switch (page) {
        case '/chef1':
          allowed = userRole.includes('Chef departement');
          break;
        case '/assistant1':
          allowed = userRole.includes('Assistant dÃ©partement') || userRole.includes('Chef departement');
          break;
        case '/commercial':
          allowed = userRole.includes('Commercial') || userRole.includes('Chef departement');
          break;
        default:
          allowed = false;
      }
  
      if (allowed) {
        navigate(page);
      } else {
        Swal.fire('Access Denied', 'You are not authorized to access this page.', 'error');
      }
    };
  
    const handleLogout = (event) => {
      event.preventDefault();
      Swal.fire({
        title: 'Are you sure?',
        text: 'Do you really want to log out?',
        icon: 'warning',
        showCancelButton: true,
        confirmButtonColor: '#3085d6',
        cancelButtonColor: '#d33',
        confirmButtonText: 'Yes, log out!'
      }).then((result) => {
        if (result.isConfirmed) {
          const token = localStorage.getItem('token');
          if (!token) {
            console.error('No token found in localStorage');
            return;
          }
  
          fetch('http://localhost:8080/api/logout', {
            method: 'GET',
            headers: {
              'Authorization': `Bearer ${token}`,
              'Content-Type': 'application/json',
              'Accept': 'application/json',
            },
          })
            .then(response => {
              if (response.status === 401) {
                throw new Error('Unauthorized');
              }
              return response.json();
            })
            .then(data => {
              if (data.status) {
                localStorage.removeItem('token');
                navigate('/login');
              }
            })
            .catch(error => {
              console.error('Error:', error);
              navigate('/login');
            });
        }
      });
    };
  
    return (
      <div id="page-container" className="sidebar-o sidebar-dark enable-page-overlay side-scroll page-header-fixed main-content-narrow">
        <aside id="side-overlay">
          <div className="block-content tab-content overflow-hidden">
            <div className="tab-pane pull-x fade fade-left show active" id="so-overview" role="tabpanel" aria-labelledby="so-overview-tab" tabIndex="0">
              <div className="block block-transparent">
                <div className="block-content">
                </div>
              </div>
            </div>
          </div>
        </aside>
  
        <nav id="sidebar" aria-label="Main Navigation">
          <div className="content-header">
            <a className="fw-semibold text-dual" href="/">
              <span className="smini-visible">
                <i className="fa fa-circle-notch text-primary"></i>
              </span>
              <span className="smini-hide fs-5 tracking-wider">Transit Master</span>
            </a>
  
            <div>
              <button type="button" className="btn btn-sm btn-alt-secondary" onClick={toggleDarkMode}>
                <i className="far fa-moon"></i>
                <span className="visually-hidden">Toggle Dark Mode</span>
              </button>
              <a className="d-lg-none btn btn-sm btn-alt-secondary ms-1" data-toggle="layout" data-action="sidebar_close" href="#!">
                <i className="fa fa-fw fa-times"></i>
              </a>
            </div>
          </div>
          <div className="js-sidebar-scroll">
            <div className="content-side">
              <ul className="nav-main">
                <li className="nav-main-item">
                  <a className="nav-main-link" href="/">
                    <i className="nav-main-link-icon si si-speedometer"></i>
                    <span className="nav-main-link-name">Dashboard</span>
                  </a>
                </li>
  
                <li className="nav-main-heading">Departement</li>
                <li className="nav-main-item">
                  <a className="nav-main-link nav-main-link-submenu" data-toggle="submenu" aria-haspopup="true" aria-expanded="false" href="#">
                    <i className="nav-main-link-icon si si-energy"></i>
                    <span className="nav-main-link-name">Departement commercial</span>
                  </a>
                  <ul className="nav-main-submenu">
                    <li className="nav-main-item">
                      <a className="nav-main-link" href="#!" id="nav-chef" onClick={() => navigateTo('/chef1')}>
                        <span className="nav-main-link-name">Chef departement</span>
                      </a>
                    </li>
                    <li className="nav-main-item">
                      <a className="nav-main-link" href="#!" id="nav-assistant" onClick={() => navigateTo('/assistant1')}>
                        <span className="nav-main-link-name">Assistant dÃ©partement</span>
                      </a>
                    </li>
                    <li className="nav-main-item">
                      <a className="nav-main-link" href="#!" id="nav-commercial" onClick={() => navigateTo('/commercial')}>
                        <span className="nav-main-link-name">Commercial</span>
                      </a>
                    </li>
                  </ul>
                </li>
              </ul>
            </div>
          </div>
        </nav>
  
        <header id="page-header">
          <div className="content-header">
            <div className="d-flex align-items-center">
              <button type="button" className="btn btn-sm btn-alt-secondary me-2 d-lg-none" data-toggle="layout" data-action="sidebar_toggle">
                <i className="fa fa-fw fa-bars"></i>
              </button>
              <button type="button" className="btn btn-sm btn-alt-secondary d-md-none" data-toggle="layout" data-action="header_search_on">
                <i className="fa fa-fw fa-search"></i>
              </button>
            </div>
  
            <div className="d-flex align-items-center">
              <div className="dropdown d-inline-block ms-2">
                <button type="button" className="btn btn-sm btn-alt-secondary d-flex align-items-center" id="page-header-user-dropdown" data-bs-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                  <img className="rounded-circle" src="assets/media/avatars/avatar10.jpg" alt="Header Avatar" style={{ width: 21 }} />
                  <span className="d-none d-sm-inline-block ms-2">{userName}</span>
                  <i className="fa fa-fw fa-angle-down d-none d-sm-inline-block opacity-50 ms-1 mt-1"></i>
                </button>
                <div className="dropdown-menu dropdown-menu-md dropdown-menu-end p-0 border-0" aria-labelledby="page-header-user-dropdown">
                  <div className="p-3 text-center bg-body-light border-bottom rounded-top">
                    <img className="img-avatar img-avatar48 img-avatar-thumb" src="assets/media/avatars/avatar10.jpg" alt="" />
                    <p className="mt-2 mb-0 fw-medium">{userFullName}</p>
                    <p className="mt-2 mb-0 fw-medium">{userRole.join(', ')}</p>
                  </div>
                  <div className="p-2">
                    <a id="logout-link" className="dropdown-item d-flex align-items-center justify-content-between" href="#!" onClick={handleLogout}>
                      <span className="fs-sm fw-medium">Log Out</span>
                      <i className="fa fa-fw fa-sign-out-alt ms-1"></i>
                    </a>
                  </div>
                </div>
              </div>
              <button type="button" className="btn btn-sm btn-alt-secondary me-2 d-lg-none" data-toggle="layout" data-action="sidebar_close">
                <i className="fa fa-fw fa-times"></i>
              </button>
            </div>
          </div>
  
          <div id="page-header-search" className="overlay-header bg-body-extra-light">
            <div className="content-header">
              <form className="w-100" method="POST">
                <div className="input-group input-group-sm">
                  <input type="text" className="form-control form-control-alt" placeholder="Search or hit ESC.." id="page-header-search-input" name="page-header-search-input" />
                  <button type="button" className="btn btn-alt-danger" data-toggle="layout" data-action="header_search_off">
                    <i className="fa fa-fw fa-times"></i>
                  </button>
                </div>
              </form>
            </div>
          </div>
        </header>
          {/* Main Container */}
    <main id="main-container">
    {/* Hero Content */}
    <div className="bg-primary-dark">
      <div className="content content-full text-center pt-7 pb-5">
        <h1 className="h2 text-white mb-2">
          Bienvenue dans la page du departement commercial
        </h1>
        <h2 className="h4 fw-normal text-white-75">
          Veuillez entrer dans votre section en fonction de votre role !
        </h2>
      </div>
    </div>
  </main>
  {/* END Main Container */}

  {/* Footer */}
  <footer id="page-footer" className="bg-body-light">
    <div className="content py-3">
      <div className="row fs-sm">
        <div className="col-sm-6 order-sm-1 py-1 text-center text-sm-start">
          <a className="fw-semibold" href="https://pixelcave.com/products/oneui" target="_blank" rel="noopener noreferrer">Transit master</a> &copy; <span data-toggle="year-copy"></span>
        </div>
      </div>
    </div>
  </footer>
  {/* END Footer */}
    </div>


  );
};

export default Departement1;
