import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Swal from 'sweetalert2';
import './styled.css';
import ProfileDropdown from './ProfileDropdown';

const Departement2 = () => {
  const [darkMode, setDarkMode] = useState(false);
  const [user, setUser] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    const token = sessionStorage.getItem('token');
    if (!token) {
      console.error('Token is missing');
      return;
    }

    const fetchUserData = async () => {
      const userId = sessionStorage.getItem('userId');
      if (!userId) {
        console.error('User ID is missing');
        return;
      }

      try {
        const response = await fetch(`http://localhost:8000/api/users/${userId}`, {
          headers: {
            'Authorization': 'Bearer ' + token,
          }
        });

        if (!response.ok) {
          throw new Error('Failed to fetch user data');
        }

        const user = await response.json();
        setUser(user);
        console.log(user);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, []);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
    document.body.classList.toggle('dark-mode', !darkMode);
  };

  const navigateTo = (path) => {
    if (Object.keys(user).length === 0) {
      Swal.fire({
        icon: 'info',
        title: 'Chargement en cours...',
        text: 'Veuillez patienter.',
      });
      return;
    }

    navigate(path);
  };

  const userRole = user.data?.role || [];

  return (
    <div className={darkMode ? 'departement2 dark-mode' : 'departement2'}>
      <div className="navbar">
        <h1>Département 2</h1>
        <div className="navbar-buttons">
          <button onClick={() => navigateTo('/dashboard')}>Dashboard</button>
          <button onClick={() => navigateTo('/profile')}>Profil</button>
          <ProfileDropdown user={user} />
          <button onClick={toggleDarkMode}>Mode Sombre</button>
        </div>
      </div>
      <div className="content">
      <div className="dossier-list">
      {dossiers.map((dossier) => (
        <div key={dossier.id} className="dossier-item">
          <img src="dossier-closed.png" alt="Dossier fermé" className="dossier-image" />
          <img src="dossier-open.png" alt="Dossier ouvert" className="dossier-image-open" />
          <div className="dossier-actions">
            <button className="action-button" onClick={() => handleRead(dossier.id)}>
              <img src="read-icon.png" alt="Lire" />
            </button>
            <button className="action-button" onClick={() => handleEdit(dossier.id)}>
              <img src="edit-icon.png" alt="Modifier" />
            </button>
            <button className="action-button" onClick={() => handleSend(dossier.id)}>
              <img src="send-icon.png" alt="Envoyer" />
            </button>
          </div>
        </div>
      ))}
    </div>
      </div>
    </div>
  );
};

export default Departement2;
