import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Swal from 'sweetalert2';
import './styled.css';
import ProfileDropdown from './ProfileDropdown';

const Departement1 = () => {
  const [darkMode, setDarkMode] = useState(false);
  const [user, setUser] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    const token = sessionStorage.getItem('token');
    if (!token) {
      console.error('Token is missing');
      return;
    }

    const fetchUserData = async () => {
      const userId = sessionStorage.getItem('userId');
      if (!userId) {
        console.error('User ID is missing');
        return;
      }

      try {
        const response = await fetch(`http://localhost:8080/api/users/${userId}`, {
          headers: {
            'Authorization': 'Bearer ' + token,
          }
        });
        if (!response.ok) {
          throw new Error('Failed to fetch user data');
        }
        const user = await response.json();
        setUser(user);
        console.log(user);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, []);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
    document.body.classList.toggle('dark-mode', !darkMode);
  };

  const renderMenuItem = (path, role) => {
    if (user.data && user.data.role && user.data.role.includes(role)) {
      return (
        <a className="nav-main-link" href="#!" onClick={() => navigateTo(path)}>
          {/* ... existing link content ... */}
        </a>
      );
    }
    return null;
  };

  return (
    <div id="page-container" className="sidebar-o sidebar-dark enable-page-overlay side-scroll page-header-fixed main-content-narrow">
      {/* Sidebar and header code remains the same */}
      <nav id="sidebar" aria-label="Main Navigation">
        {/* ... existing sidebar content */}
        <div className="js-sidebar-scroll">
          <div className="content-side">
            <ul className="nav-main">
              <li className="nav-main-item">
                <a className="nav-main-link" href="be_pages_dashboard.html">
                  <i className="nav-main-link-icon si si-speedometer"></i>
                  <span className="nav-main-link-name">Dashboard</span>
                </a>
              </li>
              <li className="nav-main-heading">Departement commercial</li>
              {renderMenuItem('/chef1', 'Chef departement')}
              {renderMenuItem('/assistant1', 'Assistant departement') || renderMenuItem('/assistant1', 'Chef departement')}
              {renderMenuItem('/commercial', 'Commercial') || renderMenuItem('/commercial', 'Chef departement')}
            </ul>
          </div>
        </div>
      </nav>
      <header id="page-header">
        {/* ... existing header code ... */}
      </header>
      <main id="main-container">
        {/* ... existing main content code ... */}
      </main>
      <footer id="page-footer" className="bg-body-light">
        {/* ... existing footer code ... */}
      </footer>
    </div>
  );
};

export default Departement1;
