import React, { useState, useEffect, useRef } from 'react';

const UserDropdown = ({ user }) => {
    const [isOpen, setIsOpen] = useState(false);
    const dropdownRef = useRef(null);

    useEffect(() => {
        const handleClickOutside = (event) => {
            if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
                setIsOpen(false);
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, []);

    const toggleDropdown = () => {
        setIsOpen(!isOpen);
    };

    return (
        <div className="dropdown d-inline-block ms-2" ref={dropdownRef}>
            <button
                type="button"
                className="btn btn-sm btn-alt-secondary d-flex align-items-center"
                onClick={toggleDropdown}
                aria-expanded={isOpen}
            >
                <span className="d-none d-sm-inline-block ms-2">{user ? user.name : ''}</span>
                <i className="fa fa-fw fa-angle-down d-none d-sm-inline-block opacity-50 ms-1 mt-1"></i>
            </button>
            {isOpen && (
                <div className="dropdown-menu p-2" style={{ position: 'absolute', top: '100%', right: 0, left: 0, zIndex: 1000, backgroundColor: 'white' }}>
                    <p>{user.name}</p>
                    <p>{user.role ? user.role.join(', ') : ''}</p>
                    <a id="logout-link" className="dropdown-item d-flex align-items-center justify-content-between" href="#">
                        <span className="fs-sm fw-medium">Log Out</span>
                    </a>
                </div>
            )}
        </div>
    );
};

export default UserDropdown;
