import { Link, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import Swal from 'sweetalert2';
import './style.css';
import UserDropdown from './UserDropdown';
import './style.css';


const Departement1 = () => {
  const [darkMode, setDarkMode] = useState(false);
  const [user, setUser] = useState({});
  const [users, setUsers] = useState([]);
  const [isDepartmentOpen, setIsDepartmentOpen] = useState(false);

  const toggleDepartment = () => {
    setIsDepartmentOpen(!isDepartmentOpen);
  };

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      console.error('Token is missing');
      return;
    }

    const fetchUserData = async () => {
      const userId = localStorage.getItem('userId');
      if (!userId) {
        console.error('User ID is missing');
        return;
      }

      try {
        const response = await fetch(`http://localhost:8080/api/users/${userId}`, {
          headers: {
            'Authorization': 'Bearer ' + token,
          }
        });

        if (!response.ok) {
          throw new Error('Failed to fetch user data');
        }

        const userData = await response.json();
        setUser(userData);
        console.log(userData)
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, []);
  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
    document.body.classList.toggle('dark-mode', !darkMode);
  };

  return (
    <div id="page-container" className="sidebar-o sidebar-dark enable-page-overlay side-scroll page-header-fixed main-content-narrow">
      {/* Side Overlay */}
      <aside id="side-overlay">
        {/* Side Header */}
        <div className="block-content tab-content overflow-hidden">
          {/* Overview Tab */}
          <div className="tab-pane pull-x fade fade-left show active" id="so-overview" role="tabpanel" aria-labelledby="so-overview-tab" tabIndex="0">
            {/* Activity */}
            <div className="block block-transparent">
              <div className="block-content">
              </div>
            </div>
          </div>
        </div>
      </aside>

      <nav id="sidebar" aria-label="Main Navigation">
        {/* Side Header */}
        <div className="content-header">
          {/* Logo */}
          <a className="fw-semibold text-dual" href="index.html">
            <span className="smini-visible">
              <i className="fa fa-circle-notch text-primary"></i>
            </span>
            <span className="smini-hide fs-5 tracking-wider">Transit Master</span>
          </a>

          <div>
            <button type="button" className="btn btn-sm btn-alt-secondary" onClick={toggleDarkMode}>
              <i className="far fa-moon"></i>
              {/* Visually hide the button text */}
              <span className="visually-hidden">Toggle Dark Mode</span>
            </button>
            <a className="d-lg-none btn btn-sm btn-alt-secondary ms-1" data-toggle="layout" data-action="sidebar_close" href="#!">
              <i className="fa fa-fw fa-times"></i>
            </a>
          </div>
        </div>
        <div className="js-sidebar-scroll">
      <div className="content-side">
        <ul className="nav-main">
