import { Link } from "react-router-dom";
import { useEffect } from 'react';

const Dashboard = () => {
    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            window.location.href = '/aindex.html';
            return;
        }

        fetch('http://localhost:8080/api/verify-token', {
            method: 'GET',
            headers: {
                'Authorization': 'Bearer ' + token,
            }
        })
        .then(response => response.json())
        .then(data => {
            if (data.status && !data.isAdmin) {
                window.location.href = '/aindex.html';
                return;
            }

            const userId = localStorage.getItem('userId');
            if (userId) {
                fetch(`http://localhost:8080/api/users/${userId}`)
                    .then(response => response.json())
                    .then(data => {
                        if (data.status) {
                            const user = data.data;
                            document.getElementById('user-name').textContent = user.name;
                            document.getElementById('user-fullname').textContent = `${user.name} `;
                            document.getElementById('user-role').textContent = ` ${user.role.join(', ')}`;
                        } else {
                            console.error('Failed to fetch user details:', data.message);
                        }
                    })
                    .catch(error => console.error('Error:', error));
            }

            fetch('http://localhost:8080/api/users', {
                method: 'GET',
                headers: {
                    'Authorization': 'Bearer ' + localStorage.getItem('token'),
                }
            })
            .then(response => response.json())
            .then(data => {
                if (data.status) {
                    const tableBody = document.querySelector('#userTable tbody');
                    tableBody.innerHTML = '';
                    data.data.sort((a, b) => a.id - b.id);

                    data.data.forEach(user => {
                        if (user.email === 'admin@itsolutionstuff.com') {
                            return;
                        }

                        const tr = document.createElement('tr');

                        tr.innerHTML = `
                            <td className="text-center">
                                <img className="img-avatar img-avatar48" src="assets/media/avatars/avatar1.jpg" alt="">
                            </td>
                            <td className="fw-semibold fs-sm">
                                <a href="be_pages_generic_profile.html">${user.name}</a>
                            </td>
                            <td className="fs-sm">${user.surname}</td>
                            <td>
                                <span className="fs-xs fw-semibold d-inline-block py-1 px-3 rounded-pill bg-primary-light text-primary">${user.departement.join(', ')}</span>
                            </td>
                            <td>
                                <span className="fs-xs fw-semibold d-inline-block py-1 px-3 rounded-pill bg-info-light text-info">${user.role.join(', ')}</span>
                            </td>
                            <td className="fs-sm">${user.phone_number}</td>
                            <td className="fs-sm">${user.email}</td>
                            <td className="text-center">
                                <div className="btn-group">
                                    <a href="aedit.html?id=${user.id}" className="btn btn-sm btn-alt-secondary edit-button" data-bs-toggle="tooltip" title="Edit">
                                        <i className="fa fa-fw fa-pencil-alt"></i>
                                    </a>
                                    <button type="button" className="btn btn-sm btn-alt-secondary delete-button" data-id="${user.id}" data-bs-toggle="tooltip" title="Delete">
                                        <i className="fa fa-fw fa-times"></i>
                                    </button>
                                </div>
                            </td>
                        `;

                        tableBody.appendChild(tr);
                    });

                    document.getElementById("logout-link").addEventListener("click", function(event) {
                        event.preventDefault();
                        const confirmed = window.confirm('Do you really want to log out?');
                        if (confirmed) {
                            const token = localStorage.getItem('token');
                            if (!token) {
                                console.error('No token found in localStorage');
                                return;
                            }

                            fetch('http://localhost:8080/api/logout', {
                                method: 'GET',
                                headers: {
                                    'Authorization': 'Bearer ' + token,
                                    'Content-Type': 'application/json',
                                    'Accept': 'application/json',
                                },
                            })
                            .then(response => {
                                if (response.status === 401) {
                                    throw new Error('Unauthorized');
                                }
                                return response.json();
                            })
                            .then(data => {
                                if (data.status) {
                                    localStorage.removeItem('token');
                                    window.location.href = '/aindex.html';
                                }
                            })
                            .catch(error => {
                                console.error('Error:', error);
                                window.location.href = '/aindex.html';
                            });
                        }
                    });

                    document.querySelectorAll('.delete-button').forEach(button => {
                        button.addEventListener('click', function() {
                            const confirmed = window.confirm('You will not be able to recover this user. Do you want to proceed?');
                            if (confirmed) {
                                const userId = this.getAttribute('data-id');
                                fetch(`http://localhost:8080/api/users/${userId}`, {
                                    method: 'DELETE',
                                    headers: {
                                        'Authorization': 'Bearer ' + localStorage.getItem('token')
                                    }
                                })
                                .then(response => response.json())
                                .then(result => {
                                    if (result.status) {
                                        this.closest('tr').remove();
                                    }
                                })
                                .catch(error => console.error('Error deleting user:', error));
                            }
                        });
                    });

                } else {
                    alert('Failed to fetch user data.');
                }
            })
            .catch(error => console.error('Error fetching data:', error));
        })
        .catch(error => console.error('Error:', error));
    }, []);

    return (
        <div>
            {/* Your JSX content */}
        </div>
    );
}

export default Dashboard;
