import React, { useState } from 'react';
import { TextField, Button, Container, Paper, Typography, FormControl, InputLabel, Select, MenuItem } from '@material-ui/core';
import Swal from 'sweetalert2';
import './MyForm.css';
import { useNavigate } from 'react-router-dom';

const MyForm = () => {
  const [step, setStep] = useState(1);
  const [formValues, setFormValues] = useState({
    exportateur: '',             
    destinataire: '',            
    date_ouverture: '',          
    provenance: '',              
    regime_client: '',          
    mode_transport: '',         
    num_dr_gls: '',             
    num_lta_bl: '',             
    nombre_total_colis: '',     
    poids_total_brut: '',       
    poids_total_net: '',        
    volume_total: '',           
    description: '',            
    date_estimative_darrivee: '',
    date_reele_darrivee: '',     
    cree_par: ''
  });

  const navigate = useNavigate();

  // Vérification de l'ID du dossier stocké localement au chargement du composant
  useState(() => {
    const storedDossierId = localStorage.getItem('dossierId');
    if (storedDossierId) {
      setStep(1); // Passer directement à l'étape 3 si un ID de dossier est trouvé
    }
  }, []); // Utiliser une dépendance vide pour s'assurer que cela se produit une seule fois au chargement du composant

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormValues((prevValues) => ({
      ...prevValues,
      [name]: value,
    }));
  };

  const handleNextStep = (e) => {
    e.preventDefault();
    const currentDate = new Date().toISOString().slice(0, 10);
    setFormValues(prevValues => ({
      ...prevValues,
      date_transmission_operation: currentDate
    }));

    Swal.fire({
      title: 'Étape suivante',
      text: 'Vous allez passer à l\'étape suivante',
      timer: 1000,
      showConfirmButton: false,
      timerProgressBar: true,
    }).then(() => {
      setStep((prevStep) => prevStep + 1);
    });
  };

  const handlePreviousStep = (e) => {
    e.preventDefault();
    setStep((prevStep) => prevStep - 1);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    fetch('http://localhost:8000/api/dossiers', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(formValues),
    })
    .then(response => {
      if (!response.ok) {
        throw new Error('Erreur lors de la soumission du formulaire');
      }
      return response.json();
    })
    .then(data => {
      // Enregistrement de l'ID du dossier dans le stockage local
      localStorage.setItem('dossierId', data.id_dossier);
      setStep((prevStep) => prevStep + 1);      
      Swal.fire({
        title: 'Succès !',
        text: 'Données enregistrées avec succès.',
        icon: 'success',
        timer: 1000,
        showConfirmButton: false,
      }).then(() => {
        // Naviguer vers la page de fichiers
        navigate(`/fichier`);
      });
    })
    .catch(error => {
      console.error('Erreur:', error);
      Swal.fire({
        title: 'Erreur',
        text: 'Une erreur s\'est produite lors de l\'enregistrement des données.',
        icon: 'error',
        timer: 3000,
        showConfirmButton: false,
      });
    });
  }
  return (
    <Container maxWidth="sm" className="form-container">
      <Paper elevation={3} className="styled-paper">
        <Typography variant="h5" gutterBottom className="form-title">
          <center>Étape {step}</center>
        </Typography>
        <form onSubmit={step === 3 ? handleSubmit : handleNextStep}>
          {step === 1 && (
            <>
              <center>Exportateur</center>
              <TextField
                label=""
                name="exportateur"
                value={formValues.exportateur}
                onChange={handleChange}
                fullWidth
                className="form-field"
                InputProps={{ disableUnderline: true }}
              />
              <center>Destinataire</center>
              <TextField
                label=""
                name="destinataire"
                value={formValues.destinataire}
                onChange={handleChange}
                fullWidth
                className="form-field"
                InputProps={{ disableUnderline: true }}
              />
              <center>Régime client</center>
              <TextField
                label=""
                name="regime_client"
                value={formValues.regime_client}
                onChange={handleChange}
                fullWidth
                className="form-field"
                InputProps={{ disableUnderline: true }}
              />
              <center>N° DR GLS</center>
              <TextField
                label=""
                name="num_dr_gls"
                value={formValues.num_dr_gls}
                onChange={handleChange}
                fullWidth
                className="form-field"
                InputProps={{ disableUnderline: true }}
              />
              <center>Provenance</center>
              <TextField
                label=""
                name="provenance"
                value={formValues.provenance}
                onChange={handleChange}
                fullWidth
                className="form-field"
                InputProps={{ disableUnderline: true }}
              />
              <Button
                type="submit"
                variant="contained"
                color="primary"
                fullWidth
                className="submit-button"
              >
                Suivant
              </Button>
            </>
          )}
          {step === 2 && (
            <>
              <center>Mode de transport</center>
              <FormControl fullWidth className="form-field">
                <InputLabel>Choisir un mode de transport</InputLabel>
                <Select
                  labelId="mode_transport"
                  name="mode_transport"
                  value={formValues.mode_transport}
                  onChange={handleChange}
                  inputProps={{
                    name: "mode_transport",
                    id: "mode_transport",
                    disableUnderline: true
                  }}
                >
                  <MenuItem value="Maritime">Maritime</MenuItem>
                  <MenuItem value="Aérien">Aérien</MenuItem>
                </Select>
              </FormControl>
              <center>N° LTA /BL</center>
              <TextField
                label=""
                name="num_lta_bl"
                value={formValues.num_lta_bl}
                onChange={handleChange}
                fullWidth
                className="form-field"
                InputProps={{ disableUnderline: true }}
              />
              <center>Nombre Total de Colis</center>
              <TextField
                label=""
                name="nombre_total_colis"
                value={formValues.nombre_total_colis}
                onChange={handleChange}
                fullWidth
                className="form-field"
                InputProps={{ disableUnderline: true }}
              />
              <center>Poids Total Brut en Kg</center>
              <TextField
                label=""
                name="poids_total_brut"
                value={formValues.poids_total_brut}
                onChange={handleChange}
                fullWidth
                className="form-field"
                InputProps={{ disableUnderline: true }}
              />
              <center>Poids Total Net en Kg</center>
              <TextField
                label=""
                name="poids_total_net"
                value={formValues.poids_total_net}
                onChange={handleChange}
                fullWidth
                className="form-field"
                InputProps={{ disableUnderline: true }}
              />
              <Button
                onClick={handlePreviousStep}
                variant="contained"
                color="secondary"
                fullWidth
                className="submit-button"
              >
                Précédent
              </Button>
              <Button
                type="submit"
                variant="contained"
                color="primary"
                fullWidth
                className="submit-button"
              >
                Suivant
              </Button>
            </>
          )}
          {step === 3 && (
            <>
              <center>Volume Total en m<sup>3</sup></center>
              <TextField
                label=""
                name="volume_total"
                value={formValues.volume_total}
                onChange={handleChange}
                fullWidth
                className="form-field"
                InputProps={{ disableUnderline: true }}
              />
              <center>Date Estimative d'Arrivée</center>
              <TextField
                label=""
                name="date_estimative_darrivee"
                type="date"
                value={formValues.date_estimative_darrivee}
                onChange={handleChange}
                fullWidth
                className="form-field"
                InputProps={{ disableUnderline: true }}
              />
              <center>Date Réelle d'Arrivée</center>
              <TextField
                label=""
                name="date_reele_darrivee"
                type="date"
                value={formValues.date_reele_darrivee}
                onChange={handleChange}
                fullWidth
                className="form-field"
                InputProps={{ disableUnderline: true }}
              />
              <center>Créé Par</center>
              <TextField
                label=""
                name="cree_par"
                value={formValues.cree_par}
                onChange={handleChange}
                fullWidth
                className="form-field"
                InputProps={{ disableUnderline: true }}
              />
              <Button
                onClick={handlePreviousStep}
                variant="contained"
                color="secondary"
                fullWidth
                className="submit-button"
              >
                Précédent
              </Button>
              <Button
                type="submit"
                variant="contained"
                color="primary"
                fullWidth
                className="submit-button"
              >
                Enregistrer
              </Button>
            </>
          )}
        </form>
      </Paper>
    </Container>
  );
};

export default MyForm;
