import { Link, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import Swal from 'sweetalert2';

const loadScript = () => {
    const script = document.createElement('script');
    script.src = '/assets/js/oneui.app.min.js'; // Adjust the path as needed
    script.async = true;

    $(document).ready(() => {
        $('body').append(script);
      });
    // Append the script after component mounts and DOM is ready
    document.body.appendChild(script);
};

const Dashboard = () => {
    const [user, setUser] = useState(null);
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true); // Add loading state
    const navigate = useNavigate();

    useEffect(() => {
        // Load the script after the component mounts
        loadScript();

        const token = localStorage.getItem('token');
        if (!token) {
            // Redirect to login page or show a message
            Swal.fire({
                icon: 'error',
                title: 'Unauthorized Access',
                text: 'You are not authorized to access this page.',
                showConfirmButton: false,
                timer: 1500
            });
            navigate('/login'); // Redirect to login page
            return;
        }

        fetch('http://localhost:8080/api/verify-token', {
            method: 'GET',
            headers: {
                'Authorization': 'Bearer ' + token,
            }
        })
        .then(response => response.json())
        .then(data => {
            if (data.status && !data.isAdmin) {
                // Redirect to aindex.html if the user is not an admin
                Swal.fire({
                    icon: 'error',
                    title: 'Unauthorized Access',
                    text: 'You are not authorized to access this page.',
                    showConfirmButton: false,
                    timer: 1500
                });
                navigate('/login'); // Adjust the navigation as needed
                return;
            }

            const userId = localStorage.getItem('userId');
            if (!userId) {
                console.error('User ID is missing');
                return;
            }

            fetch(`http://localhost:8080/api/users/${userId}`)
                .then(response => response.json())
                .then(data => {
                    if (data.status) {
                        setUser(data.data);
                    } else {
                        console.error('Failed to fetch user details:', data.message);
                    }
                })
                .catch(error => console.error('Error:', error));

            fetch('http://localhost:8080/api/users', {
                method: 'GET',
                headers: {
                    'Authorization': 'Bearer ' + localStorage.getItem('token'),
                }
            })
            .then(response => response.json())
            .then(data => {
                if (data.status) {
                    const filteredUsers = data.data.filter(user => user.email !== 'admin@itsolutionstuff.com');
                    setUsers(filteredUsers);
                } else {
                    console.error('Failed to fetch user data:', data.message);
                }
            })
            .catch(error => console.error('Error fetching data:', error))
            .finally(() => setLoading(false)); // Set loading to false after fetching data
        })
        .catch(error => console.error('Error:', error));

        return () => {
            window.removeEventListener('DOMContentLoaded', loadScript);
        };
    }, [navigate]);

    const handleLogout = () => {
        Swal.fire({
            title: 'Are you sure?',
            text: 'Do you really want to log out?',
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Yes, log out!'
        }).then((result) => {
            if (result.isConfirmed) {
                const token = localStorage.getItem('token');
                if (!token) {
                    console.error('No token found in localStorage');
                    return;
                }

                fetch('http://localhost:8080/api/logout', {
                    method: 'GET',
                    headers: {
                        'Authorization': 'Bearer ' + token,
                        'Content-Type': 'application/json',
                        'Accept': 'application/json',
                    },
                })
                .then(response => {
                    if (response.status === 401) {
                        throw new Error('Unauthorized');
                    }
                    return response.json();
                })
                .then(data => {
                    if (data.status) {
                        localStorage.removeItem('token');
                        // Show a message or perform any other necessary actions
                        Swal.fire({
                            icon: 'success',
                            title: 'Logged Out',
                            text: 'You have been successfully logged out.',
                            showConfirmButton: false,
                            timer: 1500
                        }).then(() => {
                            navigate('/login'); // Redirect to login page after logout
                        });
                    }
                })
                .catch(error => {
                    console.error('Error:', error);
                    // Handle unauthorized error (e.g., redirect to login page)
                });
            }
        });
    };

    const handleDelete = (userId) => {
        Swal.fire({
            title: 'Are you sure?',
            text: 'You will not be able to recover this user!',
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Yes, delete it!'
        }).then((result) => {
            if (result.isConfirmed) {
                fetch(`http://localhost:8080/api/users/${userId}`, {
                    method: 'DELETE',
                    headers: {
                        'Authorization': 'Bearer ' + localStorage.getItem('token')
                    }
                })
                .then(response => response.json())
                .then(result => {
                    if (result.status) {
                        setUsers(users.filter(user => user.id !== userId));
                        Swal.fire('Deleted!', 'User has been deleted.', 'success');
                    } else {
                        Swal.fire('Error!', 'Failed to delete user.', 'error');
                    }
                })
                .catch(error => console.error('Error deleting user:', error));
            }
        });
    };

    if (loading) {
        return <div>Loading...</div>; // Display loading state while data is being fetched
    }
    
    
    return (
        <div id="page-container" className="sidebar-o sidebar-dark enable-page-overlay side-scroll page-header-fixed main-content-narrow">

            <aside id="side-overlay">
