import React, { useState, useEffect, useRef } from 'react';
import ReactDOM from 'react-dom';
import './ProfileDropdown.css';

const ProfileDropdown = () => {
  const [isOpen, setOpen] = useState(false);
  const [items, setItems] = useState([
    { id: 0, label: "Istanbul, TR (AHL)" },
    { id: 1, label: "Paris, FR (CDG)" }
  ]);
  const [selectedItem, setSelectedItem] = useState(null);

  const dropdownRef = useRef(null);

  const toggleDropdown = () => setOpen(!isOpen);

  const handleItemClick = (id) => {
    setSelectedItem(id === selectedItem ? null : id);
    setOpen(false);
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <div ref={dropdownRef} className="dropdown">
      <div className="dropdown-header" onClick={toggleDropdown}>
        {selectedItem ? items.find(item => item.id === selectedItem).label : "Select your destination"}
        <i className={`fa fa-chevron-right icon ${isOpen && "open"}`}></i>
      </div>
      {isOpen && (
        <div className="dropdown-body">
          {items.map(item => (
            <div key={item.id} className="dropdown-item" onClick={() => handleItemClick(item.id)}>
              <span className={`dropdown-item-dot ${item.id === selectedItem && "selected"}`}>â€¢</span>
              {item.label}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

ReactDOM.render(
  <ProfileDropdown />,
  document.getElementById('root')
);

export default ProfileDropdown;
