import React, { useState } from 'react';
import PropTypes from 'prop-types';
import ReactDOM from 'react-dom';
import './ProfileDropdown.css';

const ProfileDropdown = () => {
    const [open, setOpen] = useState(false);
    const [selected, setSelected] = useState(props.initial || -1);
  
    const toggleDropdown = () => {
      setOpen(!open);
    };
  
    const handleClick = (i) => {
      setSelected(i);
    };
  
    const renderOptions = () => {
      if (!props.options) {
        return;
      }
  
      return props.options.map((option, i) => {
        return (
          <li
            onClick={(evt) => handleClick(i)}
            key={i}
            className={
              'dropdown__list-item ' +
              (i === selected ? 'dropdown__list-item--active' : '')
            }
          >
            {option}
          </li>
        );
      });
    };
  
    return (
      <div className="dropdown">
        <div
          onClick={() => toggleDropdown()}
          className="dropdown__toggle dropdown__list-item"
        >
          {props.title}
          <i className="fa fa-angle-down" aria-hidden="true"></i>
        </div>
        <ul className={'dropdown__list ' + (open ? 'dropdown__list--active' : '')}>
          {renderOptions()}
        </ul>
      </div>
    );
  };
  
  Dropdown.propTypes = {
    title: PropTypes.string,
    options: PropTypes.array,
  };
  
  const options = ['Apple', 'Orange', 'Pear', 'Mango'];
  
  export default ProfileDropdown;
  
  ReactDOM.render(
    <ProfileDropdown title="Dropdown Menu" options={options} />,
    document.getElementById('mount')
  );
