import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './DepartmentcommercialMenu.css';
const DepartmentcommercialMenu = () => {
  const [isDepartmentOpen, setIsDepartmentOpen] = useState(false);
  const navigate = useNavigate();

  const toggleDepartment = () => {
    setIsDepartmentOpen(!isDepartmentOpen);
    console.log(`Department menu is now ${isDepartmentOpen ? 'closed' : 'open'}`);
  };

  const navigateTo = (path) => {
    navigate(path);
  };

  return (
    <li className={`nav-main-item ${isDepartmentOpen ? 'active' : ''}`}>
      <a
        className="nav-main-link nav-main-link-submenu"
        data-toggle="submenu"
        aria-haspopup="true"
        aria-expanded={isDepartmentOpen ? 'true' : 'false'}
        href="#"
        onClick={toggleDepartment}
      >
        <i className="nav-main-link-icon si si-energy"></i>
        <span className="nav-main-link-name">Departement commercial</span>
      </a>
      <ul className="nav-main-submenu">
        <li className="nav-main-item">
          <NavLink className="nav-main-link" to="/chef1" onClick={navigateTo('/chef1')}>
            <span className="nav-main-link-name">Chef departement</span>
          </NavLink>
        </li>
        <li className="nav-main-item">
          <NavLink className="nav-main-link" to="/assistant1" onClick={navigateTo('/assistant1')}>
            <span className="nav-main-link-name">Assistant d√©partement</span>
          </NavLink>
        </li>
        <li className="nav-main-item">
          <NavLink className="nav-main-link" to="/commercial" onClick={navigateTo('/commercial')}>
            <span className="nav-main-link-name">Commercial</span>
          </NavLink>
        </li>
      </ul>
    </li>
  );
};

export default DepartmentcommercialMenu;
