            zIndex: '1001',
            width: '80%',
            maxWidth: '800px',
        }}>
            <button style={{
                position: 'absolute',
                top: '10px',
                right: '10px',
                backgroundColor: 'transparent',
                border: 'none',
                fontSize: '1.5em',
                cursor: 'pointer',
                color: '#333',
            }} onClick={toggleRegistrationForm}>
