                    )}
                    {/* Add similar blocks for other departments */}
                    {formData.department === '2' && (
                        <>
                            <label style={{ marginBottom: '5px', color: '#555' }}>Role</label>
                            <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                                <div>
                                    <input
                                        type="checkbox"
