const EditForm = ({ userId, toggleEditForm }) => {
    const [userData, setUserData] = useState({
        name: '',
        surname: '',
        email: '',
        phone_number: ''
    });

    useEffect(() => {
        // Fetch user data and populate the form
        fetch(`http://localhost:8080/api/users/${userId}`, {
            headers: {
                'Authorization': 'Bearer ' + localStorage.getItem('token')
            }
        })
        .then(response => response.json())
        .then(data => {
            if (data.status) {
                const user = data.data;
                setUserData({
                    name: user.name,
                    surname: user.surname,
                    email: user.email,
                    phone_number: user.phone_number
                });
            } else {
                console.error('Failed to fetch user details:', data.message);
            }
        })
        .catch(error => console.error('Error:', error));
    }, [userId]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUserData({ ...userData, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Update user data logic here
    };

    return (
        <div className="edit-form">
            <div className="exit-button" onClick={toggleEditForm}>×</div>
            <form onSubmit={handleSubmit}>
                <div className="col-12">
                    <label htmlFor="name">Name</label>
                    <input
                        type="text"
                        className="form-control form-control-alt"
                        id="name"
                        name="name"
                        value={userData.name}
                        onChange={handleChange}
                    />
                </div>
                <div className="col-12">
                    <label htmlFor="surname">Surname</label>
                    <input
                        type="text"
                        className="form-control form-control-alt"
                        id="surname"
                        name="surname"
                        value={userData.surname}
                        onChange={handleChange}
                    />
                </div>
                <div className="col-12">
                    <label htmlFor="email">Email</label>
                    <input
                        type="email"
                        className="form-control form-control-alt"
                        id="email"
                        name="email"
                        value={userData.email}
                        onChange={handleChange}
                    />
                </div>
                <div className="col-12">
                    <label htmlFor="phone">Phone Number</label>
                    <input
                        type="text"
                        className="form-control form-control-alt"
                        id="phone"
                        name="phone_number"
                        value={userData.phone_number}
                        onChange={handleChange}
                    />
                </div>
                <div className="col-12">
                    <button type="submit" className="btn btn-dark">Update</button>
                </div>
            </form>
        </div>
    );
};

const UserEditButton = ({ userId }) => {
    const [showEditForm, setShowEditForm] = useState(false);

    const toggleEditForm = () => setShowEditForm(!showEditForm);

    return (
        <>
            <button
                className="btn btn-sm btn-alt-secondary edit-button me-1"
                onClick={toggleEditForm}
                data-bs-toggle="tooltip"
                title="Edit"
            >
                <i className="fa fa-fw fa-pencil-alt"></i>
            </button>
            {showEditForm && <EditForm userId={userId} toggleEditForm={toggleEditForm} />}
            {showEditForm && <div className="modal-overlay"></div>}
            <style>
                {`
                    .modal-overlay {
                        position: fixed;
                        top: 0;
                        left: 0;
                        width: 100%;
                        height: 100%;
                        background-color: rgba(0, 0, 0, 0.5);
                        backdrop-filter: blur(5px);
                        z-index: 1000;
                    }

                    .edit-form {
                        position: fixed;
                        top: 50%;
                        left: 50%;
                        transform: translate(-50%, -50%);
                        background-color: white;
                        padding: 20px;
                        border-radius: 5px;
                        z-index: 1001;
                    }

                    .exit-button {
                        position: absolute;
                        top: 10px;
                        right: 10px;
                        cursor: pointer;
                    }
                `}
            </style>
        </>
    );
};



export default Dashboard;
